package com.TNS.day2;

public class ThirdProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Loop in java
		// while loop
		int i=0,j=0,k=0;
	    while (i<=10)
	    {
	        System.out.println(i);
	        i++;
	    }
	    // do while
	    do
	     {
	        System.out.println(j);
	        j++;
	     }while(j<=15);
	    
	    // for loop
	    for (int k=0;k<=10;k++){
	         System.out.println(k);
	    }
	    

	}

}
